INSERT INTO `wd_options` VALUES ('79', 'widget_text', 'a:8:{i:1;a:0:{}i:4;a:3:{s:5:\"title\";s:4:\"asdf\";s:4:\"text\";s:4:\"asdf\";s:6:\"filter\";b:0;}i:13;a:3:{s:5:\"title\";s:5:\"About\";s:4:\"text\";s:98:\"Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt.\";s:6:\"filter\";b:0;}s:12:\"_multiwidget\";i:1;i:15;a:4:{s:5:\"title\";s:0:\"\";s:4:\"text\";s:1196:\"Tại sao bạn chọn Wonder?\r\n\r\n10 năm kinh nghiệm trong ngành kiến trúc, nội thất và xây dựng, với hàng trăm khách hàng mỗi năm, Wonder là một công ty danh tiếng và đáng tin cậy trong việc cung cấp dịch vụ cho khách hàng tư nhân và doanh nghiệp.\r\n\r\nWonder áp dụng phong cách thiết kế đơn giản, bố trí nội thất thông minh, ánh sáng tràn ngập không gian với các khung cửa kính lớn và thiên về xu hướng cảnh quan màu xanh lá.\r\n\r\nCác nhóm thiết kế của Wonder quan tâm đến mọi khía cạnh từ khâu lên ý tưởng, phát triển concept đến thực tế sử dụng. Chúng tôi kết hợp giữa truyền thống và đương đại để phát triển sản phẩm. Tất cả dự án của Wonder là sự kết hợp giữa nghệ thuật kiến trúc và công năng sử dụng.\r\n\r\nHơn 50 nhân sự được tuyển chọn có năng lực, thành tích học tập cao, đầy nhiệt huyết và sáng tạo, luôn tiếp thu công nghệ, ứng dụng mới nhất nhằm mục đích hỗ trợ cho kỹ năng thiết kế, xây dựng ngày càng nhanh và đẹp hơn.\";s:6:\"filter\";b:1;s:6:\"visual\";b:1;}i:16;a:4:{s:5:\"title\";s:10:\"Liên hệ\";s:4:\"text\";s:2072:\"<strong>HỒ CHÍ MINH</strong>\r\n<div class=\"w-random\">\r\n\r\n<i class=\"bluec fa fa-map-marker\" aria-hidden=\"true\"></i>33 Lê Duẩn, Tòa nhà Deutsches Haus, P.Bến Nghé, Q.1\r\n<i class=\"fa fa-mobile redc\" aria-hidden=\"true\"></i><a href=\"tel:+84901829879\">+84 90 182 9879</a> - <i class=\"redc fa fa-phone\" aria-hidden=\"true\"></i><i class=\"yellowc fa fa-envelope\" aria-hidden=\"true\"></i>DeutschesHaus@wonder.vn\r\n\r\n<i class=\"bluec fa fa-map-marker\" aria-hidden=\"true\"></i>11 Nguyễn Thị Minh Khai, P.Bến Nghé, Q.1\r\n<i clas