INSERT INTO `wd_posts` VALUES ('368', '1', '2018-12-17 00:47:20', '2018-12-16 17:47:20', '<h4>Văn hóa công ty</h4>\r\nChúng tôi quan tâm đến từng cá nhân trong công ty về vấn đề sức khỏe, hoàn cảnh… Để có những hành động thiết thực cho từng cá nhân, giúp mọi người có thể yên tâm hơn và dành tâm trí để hoàn thành công việc một cách tốt nhất.\r\nNgoài khoản tiền lương, còn có thêm thưởng, phạt mỗi tháng dựa trên kết quả của từng nhóm làm việc.\r\nGiải trí, du lịch theo định kỳ  là điều không thể thiếu tại công ty của chúng tôi.', 'Giới thiệu', '', 'publish', 'closed', 'closed', '', 'gioi-thieu', '', '', '2018-12-17 00:47:20', '2018-12-16 17:47:20', '', '0', 'http://localhost/wonder/?page_id=368', '0', 'page', '', '0');
